import React, { Component } from 'react';
import axios from 'axios';

class DiscoverSection extends Component {
    state = {
        data: {
            "heading": "Make your Device Manage Everything For you",
            "headingTwo": "Share your photos with friends easily",
            "headingThree": "Smart and secure mobile banking",
            "headingFour": "More productivity with less effort",
            "headingFive": "Organize all your media content easily",
            "headingSix": "Work faster with powerful filters",
            "headingSeven": "Stay connected with all of your friends",
            "buttonText": "Learn More",
            "thumbOne": "/img/features_thumb.png",
            "thumbTwo": "/img/service_thumb_2.png",
            "thumbThree": "/img/welcome_mockup_2.png",
            "thumbFour": "/img/discover_thumb.png",
            "thumbFive": "/img/service_thumb_1.png",
            "thumbSix": "/img/discover_thumb_2.png"
        },
        serviceData: []
    }

    render() {
        return (
            <section className="section discover-area bg-gray overflow-hidden ptb_100">
                <div className="container">
                    <div className="row justify-content-center align-items-center">
                    <div className="col-12 col-md-8 col-lg-6 order-2 order-lg-1">
                        {/* Discover Thumb */}
                        <div className="service-thumb discover-thumb mx-auto text-center pt-5 pt-lg-0">
                        <img src={this.state.data.thumbSix} alt="" />
                        </div>
                    </div>
                    <div className="col-12 col-lg-6 order-1 order-lg-2">
                        {/* Discover Text */}
                        <div className="discover-text pt-4 pt-lg-0 px-0 px-lg-4">
                        <h2 className="pb-4">{this.state.data.headingSix}</h2>
                        {/* Service List */}
                        <ul className="service-list style-two">
                            {/* Single Service */}
                            {this.state.serviceData.map((item, idx) => {
                                return(
                                    <li key={`dfl_${idx}`} className="single-service py-2">
                                        <div className="service-text">
                                            <p>{item.text}</p>
                                        </div>
                                    </li>
                                );
                            })}
                        </ul>
                        </div>
                    </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default DiscoverSection;
